package com.example.demo;

import java.time.LocalDate;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.CommandLineRunner;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;

import com.example.demo.auth.pojo.Role;
import com.example.demo.auth.pojo.User;
import com.example.demo.auth.service.RoleService;
import com.example.demo.auth.service.UserService;
import com.example.demo.pojo.Ingredienti;
import com.example.demo.pojo.OffertaSpeciale;
import com.example.demo.pojo.Pizza;
import com.example.demo.service.IngredientiService;
import com.example.demo.service.OffertaSpecialeService;
import com.example.demo.service.PizzaService;

@SpringBootApplication
public class SpringLaMiaPizzeriaRelazioni3Application implements CommandLineRunner{

	public static void main(String[] args) {
		SpringApplication.run(SpringLaMiaPizzeriaRelazioni3Application.class, args);
	}
	
	@Autowired
	private PizzaService pizzaService;

	@Autowired
	private OffertaSpecialeService offertaSpecialeService;
	
	@Autowired
	private IngredientiService ingredientiService;
	
	@Autowired
	private RoleService roleService;
	
	@Autowired
	private UserService userService;
	
	
	@Override
	public void run(String... args) throws Exception {
		Pizza p1 = new Pizza("margherita", "prova121 prova epar", "data:image/jpeg;base64,", 15.3f);
		
		pizzaService.save(p1);
		
		
		Pizza p2 = new Pizza("4 stagioni", "pwoadawD dawdoawd wadawn", "https://www.giallozafferano.it/images/5-560/Pizza-Romana_780x520_wm.jpg", 95.5f);
		
		pizzaService.save(p2);
		
		Pizza p3 = new Pizza("provola", "prova121 prova epar", "data:image/jpeg;base64,", 5.3f);
		
		pizzaService.save(p3);
		
		
		Pizza p4 = new Pizza("cacio e pepe", "pwoadawD dawdoawd wadawn", "https://www.giallozafferano.it/images/5-560/Pizza-Romana_780x520_wm.jpg", 42.5f);
		
		pizzaService.save(p4);
		
		
		
		OffertaSpeciale s1 = new OffertaSpeciale(LocalDate.now(), LocalDate.now(), "sconto1", 5,p1);
		
		offertaSpecialeService.save(s1);
		
		OffertaSpeciale s2 = new OffertaSpeciale(LocalDate.now(), LocalDate.now(), "sconto2", 15,p2);
		
		offertaSpecialeService.save(s2);
		
		OffertaSpeciale s3 = new OffertaSpeciale(LocalDate.now(), LocalDate.now(), "sconto3", 25,p3);
		
		offertaSpecialeService.save(s3);
		
		OffertaSpeciale s4 = new OffertaSpeciale(LocalDate.now(), LocalDate.now(), "sconto4", 45,p4);
		
		offertaSpecialeService.save(s4);
		
		
		Ingredienti i1 = new Ingredienti("pomodoro");
		
		ingredientiService.save(i1);
		
		Ingredienti i2 = new Ingredienti("mozzarella");
		ingredientiService.save(i2);
		
		Ingredienti i3 = new Ingredienti("patatine");
		ingredientiService.save(i3);
		Ingredienti i4 = new Ingredienti("olio");
		ingredientiService.save(i4);
		
		
		
		Role roleUser = new Role("USER");
		Role roleAdmin = new Role("ADMIN");
		
		roleService.save(roleUser);
		roleService.save(roleAdmin);
		
		final String pws = new BCryptPasswordEncoder().encode("pws");
		
		User userUser = new User("user", pws, roleUser);
		User userAdmin = new User("admin", pws, roleAdmin);
				
		userService.save(userUser);
		userService.save(userAdmin);
		
		
	}

}
